// BEST APPROACH: Simple, reliable CSV preparation
const items = $input.all();
if (!items || items.length === 0) {
  throw new Error('No data from Google Sheets');
}

console.log('=== PREPARING CSV FOR TRANSLATION ===');
console.log('Total items:', items.length);

// Get available columns
const headers = Object.keys(items[0].json);
console.log('Available columns:', headers);

// Find SKU column (try common names)
const possibleSkuColumns = ['SKU', 'sku', 'id', 'ID', 'product_id', 'Product ID', 'handle', 'Handle'];
let skuColumn = null;
for (const col of possibleSkuColumns) {
  if (headers.includes(col)) {
    skuColumn = col;
    console.log(`âœ… Found SKU column: ${col}`);
    break;
  }
}

// Check required columns
const requiredColumns = ['Title', 'Body (HTML)'];
const missingColumns = requiredColumns.filter(col => !headers.includes(col));
if (missingColumns.length > 0) {
  throw new Error(`Missing required columns: ${missingColumns.join(', ')}`);
}

// Build CSV headers (SKU first if found, then required columns)
const csvHeaders = [];
if (skuColumn) {
  csvHeaders.push(skuColumn);
}
csvHeaders.push('Title', 'Body (HTML)');

console.log('CSV headers:', csvHeaders);

// Create CSV content
const csvRows = [csvHeaders.join(',')];

// Process all items (not just sample)
items.forEach((item, index) => {
  const row = csvHeaders.map(header => {
    let value = String(item.json[header] || '');
    // Escape CSV special characters
    if (value.includes(',') || value.includes('"') || value.includes('\n') || value.includes('\r')) {
      value = '"' + value.replace(/"/g, '""') + '"';
    }
    return value;
  });
  csvRows.push(row.join(','));
});

const csvContent = csvRows.join('\n');
console.log(`Generated CSV: ${csvRows.length} rows (including header)`);
console.log('First few rows:', csvRows.slice(0, 3));

return {
  json: {
    csv_content: csvContent,
    total_rows: items.length,
    product_context: 'house equipment and tv dishwashers refrigerator freezer food processor air conditioning',
    sample: items.length,
    sku_column: skuColumn
  }
};

const response = $input.first().json;
if (response.success) {
  const fileId = response.file_id || '';
  return {
    json: {
      success: true,
      message: 'Translation and SEO processing completed! Ready to download.',
      file_id: fileId,
      download_url: `https://jafartrans-tags.up.railway.app/download/${fileId}`,
      translation_details: {
        total_processed: response.result?.total_processed || 0,
        translation_successful: response.result?.translation_successful || 0,
        seo_successful: response.result?.seo_successful || 0,
        failed: response.result?.failed || 0,
        output_size: response.output_size
      },
      // IMPORTANT: Include the actual translation results from API
      translation_results: response.result?.data || response.data || [],
      output_columns: response.output_columns || [
        'arabic description', 'product_title_ar', 'meta_title', 'meta_description', 'handle_ar'
      ],
      features: response.features || {
        translation: 'Arabic product descriptions and titles',
        seo: 'Meta titles, descriptions, and SEO-friendly handles',
        combined: 'All data appended to new columns in the output file'
      }
    }
  };
} else {
  return {
    json: {
      success: false,
      error: response.error || 'Translation and SEO processing failed'
    }
  };
}

// UPDATED: Include SKU in output and match by SKU
const originalRows = $('Read Google Sheets').all();
const translatedRows = $('Spreadsheet File').all();

console.log('=== TRANSLATION DATA MAPPING WITH SKU ===');
console.log('Original rows:', originalRows.length);
console.log('Translated rows:', translatedRows.length);

// Translation columns we want to extract
const translationColumns = [
  'arabic description',
  'product_title_ar', 
  'meta_title',
  'meta_description',
  'handle_ar'
];

// Safety check
if (!translatedRows.length) {
  throw new Error('No translated data found. Check if Spreadsheet File node is working correctly.');
}

// Show what columns are available in translated data
if (translatedRows.length > 0) {
  console.log('Available columns in translated data:', Object.keys(translatedRows[0].json || {}));
  console.log('Sample translated row:', JSON.stringify(translatedRows[0], null, 2));
}

// Find the best matching column (SKU, ID, etc.)
let matchColumn = null;
const possibleMatchColumns = ['SKU', 'sku', 'id', 'ID', 'product_id', 'Product ID', 'handle', 'Handle'];

if (originalRows.length > 0 && translatedRows.length > 0) {
  const originalKeys = Object.keys(originalRows[0].json || {});
  const translatedKeys = Object.keys(translatedRows[0].json || {});

  // Find a common column for matching
  for (const col of possibleMatchColumns) {
    if (originalKeys.includes(col) && translatedKeys.includes(col)) {
      matchColumn = col;
      console.log(`âœ… Found matching column: ${col}`);
      break;
    }
  }

  if (!matchColumn) {
    console.log('âš ï¸ No matching column found, using index-based matching');
  }
}

// Create the output data - IMPORTANT: Include SKU for Google Sheets update
const output = [];

if (matchColumn) {
  // MATCH BY COLUMN (Best approach)
  console.log(`ðŸ”„ Matching by ${matchColumn}...`);

  originalRows.forEach((originalRow, index) => {
    const originalValue = originalRow.json[matchColumn];
    const translatedRow = translatedRows.find(t => t.json[matchColumn] === originalValue);

    if (translatedRow) {
      // Create row with SKU + translation data
      const rowData = {
        [matchColumn]: originalValue  // Include SKU for matching in Google Sheets
      };

      translationColumns.forEach(col => {
        rowData[col] = translatedRow.json[col] || '';
      });

      output.push({ json: rowData });
      console.log(`âœ… Row ${index + 1}: Matched ${originalValue}`);
    } else {
      // No match found - create empty row with SKU
      const rowData = {
        [matchColumn]: originalValue  // Include SKU even if no translation
      };

      translationColumns.forEach(col => {
        rowData[col] = '';
      });

      output.push({ json: rowData });
      console.log(`âŒ Row ${index + 1}: No match for ${originalValue}`);
    }
  });
} else {
  // MATCH BY INDEX (Fallback)
  console.log('ðŸ”„ Using index-based matching...');

  originalRows.forEach((originalRow, index) => {
    const translatedRow = translatedRows[index] || {};

    // Try to find SKU column in original data
    let skuValue = '';
    for (const col of possibleMatchColumns) {
      if (originalRow.json[col]) {
        skuValue = originalRow.json[col];
        break;
      }
    }

    const rowData = {
      SKU: skuValue  // Include SKU for matching
    };

    translationColumns.forEach(col => {
      rowData[col] = translatedRow.json?.[col] || '';
    });

    output.push({ json: rowData });
    console.log(`ðŸ“ Row ${index + 1}: Index-based mapping (SKU: ${skuValue})`);
  });
}

console.log(`\nðŸŽ¯ Final Result:`);
console.log(`- Processed ${output.length} rows`);
console.log(`- Matching method: ${matchColumn ? `Column (${matchColumn})` : 'Index'}`);
console.log(`- Sample output:`, JSON.stringify(output[0], null, 2));

return output;

