// UPDATED: Enhanced debugging for tagging response
const fileData = $input.first();
console.log('=== TAGGING RESPONSE DEBUGGING ===');
console.log('File response received:', !!fileData.binary);

if (fileData.binary && fileData.binary.data) {
  let jsonContent;

  try {
    // Handle different binary data formats
    if (typeof fileData.binary.data === 'string') {
      jsonContent = fileData.binary.data;
    } else if (fileData.binary.data.data) {
      jsonContent = Buffer.from(fileData.binary.data.data, 'base64').toString('utf8');
    } else {
      jsonContent = fileData.binary.data.toString('utf8');
    }

    console.log('JSON content extracted, length:', jsonContent.length);

    // Fix NaN values before parsing JSON
    const cleanedJsonContent = jsonContent
      .replace(/:\s*NaN/g, ': null')  // Replace : NaN with : null
      .replace(/:\s*Infinity/g, ': null')  // Replace : Infinity with : null
      .replace(/:\s*-Infinity/g, ': null'); // Replace : -Infinity with : null

    console.log('Cleaned JSON content preview:', cleanedJsonContent.substring(0, 500) + '...');

    const response = JSON.parse(cleanedJsonContent);
    console.log('\nðŸ“Š API RESPONSE ANALYSIS:');
    console.log('Status:', response.status);
    console.log('Message:', response.message);
    console.log('Has data array:', !!response.data);
    console.log('Data type:', typeof response.data);
    console.log('Data length:', Array.isArray(response.data) ? response.data.length : 'Not an array');

    if (response.stats) {
      console.log('\nðŸ“ˆ API STATS:');
      console.log('Total products:', response.stats.total_products);
      console.log('Tagged products:', response.stats.tagged_products);
    }

    if (response.status === 'success' && response.data && Array.isArray(response.data)) {
      const taggedProducts = response.data;
      console.log('\nâœ… SUCCESS: Found tagged products:', taggedProducts.length);

      // Show sample of tagged products
      console.log('\nðŸ“‹ SAMPLE TAGGED PRODUCTS:');
      taggedProducts.slice(0, 3).forEach((product, i) => {
        console.log(`Product ${i + 1}:`, {
          Title: product.Title?.substring(0, 30) + '...',
          Type: product['Product Type'],
          HasTags: !!product.tags,
          Tags: product.tags?.substring(0, 50) + '...',
          Confidence: product.confidence
        });
      });

      // Count products with tags
      const productsWithTags = taggedProducts.filter(p => p.tags && p.tags.trim() !== '');
      console.log(`\nðŸŽ¯ TAGGING SUMMARY:`);
      console.log(`- Total products returned: ${taggedProducts.length}`);
      console.log(`- Products with tags: ${productsWithTags.length}`);
      console.log(`- Products without tags: ${taggedProducts.length - productsWithTags.length}`);

      // Return each tagged product as a separate item
      return taggedProducts.map(product => ({
        json: {
          Title: product.Title || '',
          'Product Type': product['Product Type'] || '',
          Vendor: product.Vendor || '',
          'Body (HTML)': product['Body (HTML)'] || '',
          choice_id: product.choice_id || '',
          confidence: product.confidence || 0,
          product_id: product.product_id || '',
          tags: product.tags || '',
          title: product.title || product.Title || '',
          verified: product.verified || 0
        }
      }));
    } else {
      console.log('\nâŒ FAILED: Tagging failed:', response);
      return [{
        json: {
          error: response?.message || 'Tagging failed',
          status: response?.status || 'error'
        }
      }];
    }
